INSERT INTO `script_texts` VALUES ('-1658001', 'Tiny creatures under feet, you bring Garfrost something good to eat!', null, null, null, null, null, null, null, null, '16912', '1', '0', '0', 'garfrost SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1658002', 'Will save for snack. For later.', null, null, null, null, null, null, null, null, '16913', '1', '0', '0', 'garfrost SAY_SLAY_1');
INSERT INTO `script_texts` VALUES ('-1658003', 'That one maybe not so good to eat now. Stupid Garfrost! BAD! BAD!', null, null, null, null, null, null, null, null, '16914', '1', '0', '0', 'garfrost SAY_SLAY_2');
INSERT INTO `script_texts` VALUES ('-1658004', 'Garfrost hope giant underpants clean. Save boss great shame. For later.', null, null, null, null, null, null, null, null, '16915', '1', '0', '0', 'garfrost SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1658005', 'Axe too weak. Garfrost make better and CRUSH YOU!', null, null, null, null, null, null, null, null, '16916', '1', '0', '0', 'garfrost SAY_PHASE2');
INSERT INTO `script_texts` VALUES ('-1658006', 'Garfrost tired of puny mortals. Now your bones will freeze!', null, null, null, null, null, null, null, null, '16917', '1', '0', '0', 'garfrost SAY_PHASE3');
INSERT INTO `script_texts` VALUES ('-1658007', 'Another shall take his place. You waste your time.', null, null, null, null, null, null, null, null, '16752', '1', '0', '0', 'Tyrannus SAY_TYRANNUS_DEATH');
INSERT INTO `script_texts` VALUES ('-1658010', 'Our work must not be interrupted! Ick! Take care of them!', null, null, null, null, null, null, null, null, '16926', '1', '0', '0', 'Krick SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1658011', 'Ooh...We could probably use these parts!', null, null, null, null, null, null, null, null, '16927', '1', '0', '0', 'Krick SAY_SLAY_1');
INSERT INTO `script_texts` VALUES ('-1658012', 'Arms and legs are in short supply...Thanks for your contribution!', null, null, null, null, null, null, null, null, '16928', '1', '0', '0', 'Krick SAY_SLAY_2');
INSERT INTO `script_texts` VALUES ('-1658013', 'Enough moving around! Hold still while I blow them all up!', null, null, null, null, null, null, null, null, '16929', '1', '0', '0', 'Krick SAY_BARRAGE_1');
INSERT INTO `script_texts` VALUES ('-1658014', 'Krick begins rapidly conjuring explosive mines!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'Krick SAY_BARRAGE_2');
INSERT INTO `script_texts` VALUES ('-1658015', 'Quickly! Poison them all while they\'re still close!', null, null, null, null, null, null, null, null, '16930', '1', '0', '0', 'Krick SAY_POISON_NOVA');
INSERT INTO `script_texts` VALUES ('-1658016', 'No! That one! That one! Get that one!', null, null, null, null, null, null, null, null, '16931', '1', '0', '0', 'Krick SAY_CHASE_1');
INSERT INTO `script_texts` VALUES ('-1658017', 'I\'ve changed my mind...go get that one instead!', null, null, null, null, null, null, null, null, '16932', '1', '0', '0', 'Krick SAY_CHASE_2');
INSERT INTO `script_texts` VALUES ('-1658018', 'What are you attacking him for? The dangerous one is over there,fool!', null, null, null, null, null, null, null, null, '16933', '1', '0', '0', 'Krick SAY_CHASE_3');
INSERT INTO `script_texts` VALUES ('-1658020', 'Ick begins to unleash a toxic poison cloud!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'Ick SAY_ICK_POISON_NOVA');
INSERT INTO `script_texts` VALUES ('-1658021', 'Ick is chasing you!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'Ick SAY_ICK_CHASE_1');
INSERT INTO `script_texts` VALUES ('-1658030', 'Wait! Stop! Don\'t kill me, please! I\'ll tell you everything!', null, null, null, null, null, null, null, null, '16934', '1', '0', '0', 'Krick SAY_KRICK_OUTRO_1');
INSERT INTO `script_texts` VALUES ('-1658031', 'I\'m not so naive as to believe your appeal for clemency, but I will listen.', null, null, null, null, null, null, null, null, '16611', '1', '0', '0', 'Jaina SAY_JAINA_OUTRO_2');
INSERT INTO `script_texts` VALUES ('-1658032', 'Why should the Banshee Queen spare your miserable life?', null, null, null, null, null, null, null, null, '17033', '1', '0', '0', 'Sylvanas SAY_SYLVANAS_OUTRO_2');
INSERT INTO `script_texts` VALUES ('-1658033', 'What you seek is in the master\'s lair, but you must destroy Tyrannus to gain entry. Within the Halls of Reflection you will find Frostmourne. It... it holds the truth.', null, null, null, null, null, null, null, null, '16935', '1', '0', '0', 'Krick SAY_KRICK_OUTRO_3');
INSERT INTO `script_texts` VALUES ('-1658034', 'Frostmourne lies unguarded? Impossible!', null, null, null, null, null, null, null, null, '16612', '1', '0', '0', 'Jaina SAY_JAINA_OUTRO_4');
INSERT INTO `script_texts` VALUES ('-1658035', 'Frostmourne? The Lich King is never without his blade! If you are lying to me...', null, null, null, null, null, null, null, null, '17034', '1', '0', '0', 'Sylvanas SAY_SYLVANAS_OUTRO_4');
INSERT INTO `script_texts` VALUES ('-1658036', 'I swear it is true! Please, don\'t kill me!!', null, null, null, null, null, null, null, null, '16936', '1', '0', '0', 'Krick SAY_KRICK_OUTRO_5');
INSERT INTO `script_texts` VALUES ('-1658037', 'Worthless gnat! Death is all that awaits you!', null, null, null, null, null, null, null, null, '16753', '1', '0', '0', 'Tyrannus SAY_TYRANNUS_OUTRO_7');
INSERT INTO `script_texts` VALUES ('-1658038', 'Urg... no!!', null, null, null, null, null, null, null, null, '16937', '1', '0', '0', 'Krick SAY_KRICK_OUTRO_8');
INSERT INTO `script_texts` VALUES ('-1658039', 'Do not think that I shall permit you entry into my master\'s sanctum so easily. Pursue me if you dare.', null, null, null, null, null, null, null, null, '16754', '1', '0', '0', 'Tyrannus SAY_TYRANNUS_OUTRO_9');
INSERT INTO `script_texts` VALUES ('-1658040', 'What a cruel end. Come, heroes. We must see if the gnome\'s story is true. If we can separate Arthas from Frostmourne, we might have a chance at stopping him.', null, null, null, null, null, null, null, null, '16613', '1', '0', '0', 'Jaina SAY_JAINA_OUTRO_10');
INSERT INTO `script_texts` VALUES ('-1658041', 'A fitting end for a traitor. Come, we must free the slaves and see what is within the Lich King\'s chamber for ourselves.', null, null, null, null, null, null, null, null, '17035', '1', '0', '0', 'Sylvanas SAY_SYLVANAS_OUTRO_10');
INSERT INTO `script_texts` VALUES ('-1658050', 'Your pursuit shall be in vain, adventurers, for the Lich King has placed an army of undead at my command! Behold!', null, null, null, null, null, null, null, null, '16755', '1', '0', '0', 'Tyrannus SAY_AMBUSH_1');
INSERT INTO `script_texts` VALUES ('-1658051', 'Persistent whelps! You will not reach the entrance of my lord\'s lair! Soldiers, destroy them!', null, null, null, null, null, null, null, null, '16756', '1', '0', '0', 'Tyrannus SAY_AMBUSH_2');
INSERT INTO `script_texts` VALUES ('-1658052', 'Rimefang! Trap them within the tunnel! Bury them alive!', null, null, null, null, null, null, null, null, '16757', '1', '0', '0', 'Tyrannus SAY_GAUNTLET_START');
INSERT INTO `script_texts` VALUES ('-1658053', 'Alas, brave, brave adventurers, your meddling has reached its end. Do you hear the clatter of bone and steel coming up the tunnel behind you? That is the sound of your impending demise.', null, null, null, null, null, null, null, null, '16758', '1', '0', '0', 'Tyrannus SAY_INTRO_1');
INSERT INTO `script_texts` VALUES ('-1658054', 'Ha, such an amusing gesture from the rabble. When I have finished with you, my master\'s blade will feast upon your souls. Die!', null, null, null, null, null, null, null, null, '16759', '1', '0', '0', 'Tyrannus SAY_INTRO_2');
INSERT INTO `script_texts` VALUES ('-1658055', 'I shall not fail The Lich King! Come and meet your end!', null, null, null, null, null, null, null, null, '16760', '1', '0', '0', 'Tyrannus SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1658056', 'Such a shameful display... You are better off dead!', null, null, null, null, null, null, null, null, '16761', '1', '0', '0', 'Tyrannus SAY_SLAY_1');
INSERT INTO `script_texts` VALUES ('-1658057', 'Perhaps you should have stayed in the mountains!', null, null, null, null, null, null, null, null, '16762', '1', '0', '0', 'Tyrannus SAY_SLAY_2');
INSERT INTO `script_texts` VALUES ('-1658058', 'Impossible! Rimefang... Warn...', null, null, null, null, null, null, null, null, '16763', '1', '0', '0', 'Tyrannus SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1658059', 'Rimefang, destroy this fool!', null, null, null, null, null, null, null, null, '16764', '1', '0', '0', 'Tyrannus SAY_MARK_RIMEFANG_1');
INSERT INTO `script_texts` VALUES ('-1658060', 'The frostwyrm Rimefang gazes at $N and readies an icy attack!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'Tyrannus SAY_MARK_RIMEFANG_2');
INSERT INTO `script_texts` VALUES ('-1658061', 'Power... overwhelming!', null, null, null, null, null, null, null, null, '16765', '1', '0', '0', 'Tyrannus SAY_DARK_MIGHT_1');
INSERT INTO `script_texts` VALUES ('-1658062', 'Scourgelord Tyrannus roars and swells with dark might!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'Tyrannus SAY_DARK_MIGHT_2');
INSERT INTO `script_texts` VALUES ('-1658063', 'Brave champions, we owe you our lives, our freedom... Though it be a tiny gesture in the face of this enormous debt, I pledge that from this day forth, all will know of your deeds, and the blazing path of light you cut through the shadow of this dark citadel.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'Gorkun  SAY_GORKUN_OUTRO_1');
INSERT INTO `script_texts` VALUES ('-1658064', 'This day will stand as a testament not only to your valor, but to the fact that no foe, not even the Lich King himself, can stand when Alliance and Horde set aside their differences and ---', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'Gorkun  SAY_GORKUN_OUTRO_2');
INSERT INTO `script_texts` VALUES ('-1658065', 'Heroes, to me!', null, null, null, null, null, null, null, null, '16614', '1', '0', '0', 'Jaina SAY_JAYNA_OUTRO_3');
INSERT INTO `script_texts` VALUES ('-1658066', 'Take cover behind me! Quickly!', null, null, null, null, null, null, null, null, '17037', '1', '0', '0', 'Sylvanas SAY_SYLVANAS_OUTRO_3');
INSERT INTO `script_texts` VALUES ('-1658067', 'The Frost Queen is gone. We must keep moving - our objective is near.', null, null, null, null, null, null, null, null, '16615', '0', '0', '0', 'Jaina SAY_JAYNA_OUTRO_4');
INSERT INTO `script_texts` VALUES ('-1658068', 'I thought he\'d never shut up. At last, Sindragosa silenced that long-winded fool. To the Halls of Reflection, champions! Our objective  is near... I can sense it.', null, null, null, null, null, null, null, null, '17036', '0', '0', '0', 'Sylvanas SAY_SYLVANAS_OUTRO_4');
INSERT INTO `script_texts` VALUES ('-1658069', 'I... I could not save them... Damn you, Arthas! DAMN YOU!', null, null, null, null, null, null, null, null, '16616', '0', '0', '0', 'Jaina SAY_JAYNA_OUTRO_5');
